var generators = require('yeoman-generator');
var fs = require('fs');
var configObject = {};
var models = {};
module.exports = generators.Base.extend({


    prompting: function () {
        var done = this.async();
        var _this = this;

        var modelPromptOptions = {
            type: 'list',
            name: 'model',
            message: 'enter model name',
            choices: ['one', 'two'],
            default: 'one' // Default to current folder name
        };
        var cb = function (answers) {
            _this.log(answers.model);
            if (answers.model != '') {
                models[answers.model] = {};





                _this.prompt(modelPromptOptions, cb.bind(_this));


            } else {
                console.log(models);
                fs.writeFileSync('someConfig.json', JSON.stringify(models, null, 4), 'utf8');
            }
            done();
        };


        _this.prompt(modelPromptOptions, cb);


    }


});